queries.query:
PREFIX schema: <http://www.fing.edu.uy/inco/cubes/schemas/migr_asyapp#>; 
QUERY
$C1 = ROLLUP (<http://eurostat.linked-statistics.org/data/migr_asyappctzm>, schema:citizenshipDim,schema:continent);
$C2 = ROLLUP ($C1, schema:citizenshipDim,schema:citAll);
$C3 = ROLLUP ($C2, schema:timeDim, schema:year);
$C4 = DRILLDOWN ($C3, schema:citizenshipDim,schema:continent);

PREFIX schema: <http://www.fing.edu.uy/inco/cubes/schemas/migr_asyapp#>; 
QUERY
$C1 = ROLLUP (<http://eurostat.linked-statistics.org/data/migr_asyappctzm>, schema:citizenshipDim,schema:continent);
$C2 = SLICE ($C1, D(schema:citizenshipDim));
$C3 = ROLLUP ($C2, schema:timeDim, schema:year);

PREFIX schema: <http://www.fing.edu.uy/inco/cubes/schemas/migr_asyapp#>;
PREFIX sdmx-measure: <http://purl.org/linked-data/sdmx/2009/measure#>;
PREFIX sdmx-dimension: <http://purl.org/linked-data/sdmx/2009/dimension#>;
PREFIX property: <http://eurostat.linked-statistics.org/property#>;
PREFIX geo: <http://eurostat.linked-statistics.org/dic/geo#>;
PREFIX citizen: <http://eurostat.linked-statistics.org/dic/citizen#>;
QUERY
$C1 = ROLLUP (<http://eurostat.linked-statistics.org/data/migr_asyappctzm>, schema:citizenshipDim,schema:continent);
$C2 = ROLLUP ($C1, schema:timeDim, schema:year);
$C3 = DICE ($C2, ( L(schema:continent)= citizen:AF));
$C4 = DICE ($C3, ( M(sdmx-measure:obsValue) > 5000 && (L(property:geo)= geo:BE) || L(property:geo)= geo:FR));
$C5 = DRILLDOWN ($C4, schema:citizenshipDim, property:citizen);
